import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { FaHome, FaUpload, FaList } from 'react-icons/fa';

function Navbar() {
  const location = useLocation();
  
  // Helper function to determine if a path is active
  const isActive = (path) => {
    return location.pathname === path;
  };
  
  return (
    <nav className="bg-white shadow-lg">
      <div className="container mx-auto px-4">
        <div className="flex justify-between items-center h-16">
          <div className="flex items-center">
            <Link to="/" className="text-2xl font-bold text-blue-600">
              Resume Parser
            </Link>
          </div>
          <div className="hidden md:flex space-x-6">
            <Link 
              to="/" 
              className={`flex items-center ${
                isActive('/') 
                  ? 'text-blue-600 font-medium' 
                  : 'text-gray-600 hover:text-blue-600'
              }`}
            >
              <FaHome className="mr-2" />
              Home
            </Link>
            <Link 
              to="/upload" 
              className={`flex items-center ${
                isActive('/upload') 
                  ? 'text-blue-600 font-medium' 
                  : 'text-gray-600 hover:text-blue-600'
              }`}
            >
              <FaUpload className="mr-2" />
              Upload
            </Link>
            <Link 
              to="/resumes" 
              className={`flex items-center ${
                isActive('/resumes') 
                  ? 'text-blue-600 font-medium' 
                  : 'text-gray-600 hover:text-blue-600'
              }`}
            >
              <FaList className="mr-2" />
              Resumes
            </Link>
          </div>
          
          {/* Mobile Menu Button - can be expanded with a dropdown menu if needed */}
          <div className="md:hidden flex items-center">
            <Link to="/" className={`p-2 ${isActive('/') ? 'text-blue-600' : 'text-gray-600'}`}>
              <FaHome />
            </Link>
            <Link to="/upload" className={`p-2 ${isActive('/upload') ? 'text-blue-600' : 'text-gray-600'}`}>
              <FaUpload />
            </Link>
            <Link to="/resumes" className={`p-2 ${isActive('/resumes') ? 'text-blue-600' : 'text-gray-600'}`}>
              <FaList />
            </Link>
          </div>
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
